import React, { useReducer, createContext } from 'react';
import contextReducer from './contextReducer';
import axios from 'axios';


const initialState = {
  transactions:[]
}

export const ExpenseTrackerContext = createContext(initialState.transactions);

export const Provider = ({ children }) => {
    const [transactions, dispatch] = useReducer(contextReducer, initialState.transactions);

    //Action Creators
    async function getTransactions() {
      try {
        const res = await axios.get('http://localhost:5000/api/v1/transactions');

        dispatch({
          type: 'GET_TRANSACTIONS',
          payload: res.data.data
        });
      } catch (err) {
        dispatch({
          type: 'TRANSACTION_ERROR',
          payload: err.response.data.error
        });
      }
    }

    async function deleteTransaction(id) {
      try {
        await axios.delete(`http://localhost:5000/api/v1/transactions/${id}`);

        dispatch({
          type: 'DELETE_TRANSACTION',
          payload: id
        });
      } catch (err) {
        dispatch({
          type: 'TRANSACTION_ERROR',
          payload: err.response.data.error
        });
      }
    }

    async function addTransaction(transaction) {
      const config = {
        headers: {
          'Content-Type': 'application/json'
        }
      }

      try {
        const res = await axios.post('http://localhost:5000/api/v1/transactions', transaction, config);

        dispatch({
          type: 'ADD_TRANSACTION',
          payload: res.data.data
        });
      } catch (err) {
        dispatch({
          type: 'TRANSACTION_ERROR',
          payload: err.response.data.error
        });

      }
    }



    const balance = transactions.reduce((acc, currVal) => (currVal.type === 'Expense' ? acc - currVal.amount : acc + currVal.amount), 0);

    console.log(transactions);



    return (
        <ExpenseTrackerContext.Provider value={{
            getTransactions,
            deleteTransaction,
            addTransaction,
            transactions,
            balance
        }}>
            {children}
        </ExpenseTrackerContext.Provider>);
        }
