import React, { createContext ,useState} from 'react';

import axios from 'axios';


const initialState = []



export const ExpenseTrackerContext = createContext(initialState);

export const Provider = ({ children }) => {
  const [transactions,settransactions]=useState(initialState)


    //Action Creators

    const getTransactions=async (transaction)=>{
      const response = await fetch(`http://localhost:5000/api/v1/transactions`, {
        method: 'GET', 

        headers: {
          'Content-Type': 'application/json',
          "auth-token":localStorage.getItem('token')
        },


     });
     const json=await response.json(); 

       settransactions(json)

    }

    const addTransaction=async (transaction)=>{
      const response = await fetch(`http://localhost:5000/api/v1/transactions`, {
        method: 'POST', 

        headers: {
          'Content-Type': 'application/json',
          "auth-token":localStorage.getItem('token')
        },

        body: JSON.stringify({transaction}) 
     });
     const tran=await response.json(); 
     settransactions(transactions.concat(tran))

    }

    const  deleteTransaction=async (id)=>{
      const response = await fetch(`http://localhost:5000/api/v1/transactions/${id}`, {

        method: 'DELETE', 

        headers: {
          'Content-Type': 'application/json',
          "auth-token":localStorage.getItem('token')
        },



     });
     const json= response.json(); 



      const newTransaction=transactions.filter((note)=>{return transactions._id!==id});
      settransactions(newTransaction)
    }




    // const balance = transactions.reduce((acc, currVal) => (currVal.type === 'Expense' ? acc - currVal.amount : acc + currVal.amount), 0);

    console.log(transactions);



    return (
        <ExpenseTrackerContext.Provider value={{
            getTransactions,
            deleteTransaction,
            addTransaction,
            transactions,
            // balance
        }}>
            {children}
        </ExpenseTrackerContext.Provider>
    )
}
